{
  "Day 1": {
    "Morning": {
      "9:00 AM": "Start the day at Red Square, one of the most iconic landmarks in Moscow. Explore the colorful St. Basil's Cathedral and take a stroll around the square.",
      "10:30 AM": "Visit the nearby Kremlin, a UNESCO World Heritage Site and the former residence of Russian tsars. Take a guided tour of the Armory Chamber and the Diamond Fund."
    },
    "Afternoon": {
      "1:00 PM": "Take a break for lunch at a traditional Russian restaurant, such as Cafe Pushkin or Stolovaya No. 57.",
      "2:30 PM": "Visit the Tretyakov Gallery, one of the largest and most famous art museums in Russia. The gallery features a vast collection of Russian art, including works by Kandinsky, Chagall, and Malevich."
    },
    "Evening": {
      "7:00 PM": "Enjoy a traditional Russian dinner at a restaurant like Cafe Krasny Oktyabr or Zima.",
      "9:00 PM": "Take a stroll along the Moskva River and enjoy the city's nightlife."
    }
  },
  "Day 2": {
    "Morning": {
      "9:00 AM": "Visit the Novodevichy Convent, a beautiful 16th-century monastery that served as a prison for Russian aristocrats during the Soviet era.",
      "11:00 AM": "Explore the nearby Museum of Modern Art, which features a collection of contemporary Russian art."
    },
    "Afternoon": {
      "1:00 PM": "Take a break for lunch at a modern Russian restaurant, such as White Rabbit or 50 Let Pobedy.",
      "2:30 PM": "Visit the Bolshoi Theatre, one of the most famous opera and ballet companies in the world. Take a guided tour of the theatre and learn about its history and architecture."
    },
    "Evening": {
      "7:00 PM": "Enjoy a performance at the Bolshoi Theatre, if schedules permit.",
      "9:00 PM": "Take a walk through the trendy neighborhood of Taganka, known for its street art and nightlife."
    }
  },
  "Day 3": {
    "Morning": {
      "9:00 AM": "Visit the Gorky Park, a large public park that offers a variety of activities, including boat rides, cycling, and picnicking.",
      "11:00 AM": "Explore the nearby Museum of Cosmonautics, which features a collection of space-related artifacts and exhibits."
    },
    "Afternoon": {
      "1:00 PM": "Take a break for lunch at a park cafe or food truck.",
      "2:30 PM": "Visit the Sparrow Hills, a scenic area with stunning views of the city. Take a walk along the observation deck and enjoy the views."
    },
    "Evening": {
      "6:00 PM": "Return to Red Square for a farewell dinner at a traditional Russian restaurant.",
      "8:00 PM": "End the trip with a visit to the nearby Lenin Mausoleum, a historic site that serves as the final resting place of Vladimir Lenin."
    }
  }
}
